import React, { Component } from "react";
import ThemeContext from "./themeContext";

function Button(props) {
  return (
      <ThemeContext.Consumer>
          {theme => (
              <button className={`${theme}-theme`}>Switch Theme</button>
          )}
      </ThemeContext.Consumer>)
}

export default Button;
