    render() {
        return (
            <div>
                <h3>Click heart to favorite</h3>
                <h1>
                    <span
                        onClick={this.props.toggle}
                    >
                        {this.props.on ? "❤️" : "♡"}
                    </span>
                </h1>
            </div>
        )
    }
}

const superchargedfavouritecomponent = withToggler(Favourite)
export default superchargedfavouritecomponent
