const redux = require("redux");

const action = {
  type: "INCREMENT",
};

function increment() {
  return {
    type: "INCREMENT",
  };
}

function decrement(){
    return{
        type: "DECREMENT",
    };
}

function double(){
    return{
        type: "DOUBLE",
    };
}

function half(){
    return{
        type: "HALF",
    };
}

const initialState = {
  count: 0,
};

// return new state based on the incoming action.type

/**
 * Challenge:
 *
 * Enable the ability to double and halve the count.
 * If halving, round down instead of letting your count
 * become a decimal.
 */

function reducer(state = initialState, action) {
  switch (action.type) {
    case "INCREMENT":
      return {
        count: state.count + 1,
      };
    case "DECREMENT":
      return {
        count: state.count - 1,
      };
    case "DOUBLE":
      return {
        count: state.count * 2,
      };
    case "HALF":
      return {
        count: state.count / 2,
      };
    default:
      return state;
  }
}

const store = redux.createStore(reducer);

store.subscribe(() => {
  console.log(store.getState());
});

store.dispatch({ type: "INCREMENT" });
store.dispatch({ type: "INCREMENT" });
store.dispatch({ type: "DECREMENT" });
