import React, {Component} from "react"
import Toggler from "./Toggler"

function Favourite(props) {
    return (
        <Toggler render={function(on, toggle){
            return(
                <div>
                    <h3>Click heart to favourite</h3>
                    <h1>
                <span
                    onClick={props.toggle}
                >
                    {props.on ? "❤️" : "♡"}
                </span>
                    </h1>
                </div>
            )
        }}/>
    )
}

export default Favourite
